    position: 'relative',
  },
  formBox: {
    color: "#61dafb",
    fontSize: 30,
    fontWeight: "bold",
    margin: 36,
    shadowColor: "#000",
shadowOffset: {
	width: 0,
	height: 1,
},
shadowOpacity: 0.22,
shadowRadius: 2.22,

elevation: 3,
  },
  formItems: {
    fontWeight:"bold",
    paddingVertical:6,
