import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
  },
  formBox: {
    color: "#61dafb",
    fontSize: 30,
    fontWeight: "bold",
    margin: 36,
  },
  formItems: {
    fontWeight:"bold",
    paddingVertical:6,
    paddingHorizontal:6,
    marginVertical:6,
    borderColor: 'black',
    borderWidth: 2,
    borderRadius: 8,
    shadowColor: "black",
    shadowOffset:10

  }
  

});

export default styles;
