const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
  },
  formBox: {
    color: "#61dafb",
    fontSize: 30,
    fontWeight: "bold",
    margin: 36,
    
  },
  formItems: {
    fontWeight:"bold",
    paddingVertical:6,
    paddingHorizontal:6,
    marginVertical:6,
    borderColor: 'gray',
    borderWidth: 2,
    borderRadius: 8,
  },
  button: {
    borderRadius: 16,
  }
  

});

export default styles;
