import { StyleSheet } from 'react-native';

const styles = StyleSheet.create({
  container: {
    flex: 1,
    position: 'relative',
  },
  formBox: {
    color: "#61dafb",
    fontSize: 30,
    fontWeight: "bold",
    margin: 36,
    backgroundColor:"red",
  },
  formItems: {
    fontWeight:"bold",
    paddingVertical:6,
    borderColor: 'black',
    borderStyle: 'dotted',
    borderWidth: 2,
    borderRadius: 1,
    position: 'relative',

  }
  

});

export default styles;
